import { Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs/internal/BehaviorSubject";

@Injectable({
    providedIn: 'root'
})
export class FormConfig {

    formHeader: string;

    /** makes the @currentFormHeader as an observable, which emits the current form header(updated header)
    *   and can be subscribed by any component.
    */
    public formHeaderSource = new BehaviorSubject<string>(this.formHeader);
    currentFormHeader = this.formHeaderSource.asObservable();

    get getFormHeader() {
        return this.formHeader;
    }

    set setFormHeader(formHeader: string) {
        this.formHeader = formHeader;
        this.formHeaderSource.next(this.formHeader);  
    }

}